import {ReactComponent as Linked} from '../linkedin.svg';
import { ReactComponent as Git } from '../icons8-github.svg';
import { ReactComponent as Xing } from '../xing.svg';
import { useState, useEffect } from 'react';

function Skills(props) {
  const [slider, setSlider] = useState('hidden');

  useEffect(() => {
    if(props.offsetY >5000){
      setSlider('aboutme-animation')
    }
   }, [props.offsetY, slider])

  const linkedinHandler = () => {
      var windowFeatures = "menubar=yes,location=yes,resizable=yes,scrollbars=yes,status=yes";
      window.open("http://www.linkedin.com/", "linkedin", windowFeatures);
    }
  const xingHandler = () => {
    var windowFeatures = "menubar=yes,location=yes,resizable=yes,scrollbars=yes,status=yes";
    window.open("http://www.xing.com/", "xing", windowFeatures);
  }
  const gitHandler = () => {
    var windowFeatures = "menubar=yes,location=yes,resizable=yes,scrollbars=yes,status=yes";
    window.open("http://www.github.com/", "github", windowFeatures);
  }


   return (     
   <section className=' relative h-screen'>
   <p className={` ${slider} relative  opacity-10 ml-48  text-7xl font-bold flex justify-right`}>SKILLS</p>

   <div className="ml-48 -mt-32 absolute grid grid-cols-2 gap-4">
     <div>
       <p className=' text-6xl  flex justify-right'>React</p>
       <p className=' text-6xl  flex justify-right'>Node.js</p>
     </div>
     <div>
       <p className=' text-6xl  flex justify-right'>PyTorch</p>
       <p className=' text-6xl  flex justify-right'>OpenCV</p>
     </div>
     <div>
       <p className=' mt-16 text-6xl  flex justify-right'>Responsive Design</p>
       <p className=' text-6xl  flex justify-right'>User Experience</p>
     </div>
     <div>
       <p className=' mt-16 text-6xl  flex justify-right'>ML Algorithms</p>
       <p className=' text-6xl  flex justify-right'>Embedded Systems</p>
     </div>
   </div>
   <div className=' mt-32 absolute left-0 ml-48'>
   <div className=' mt-96 flex'>
     <Linked onClick={linkedinHandler} className=' w-32 h-32'/>
     <Xing onClick={xingHandler} className='ml-16 w-32 h-32'/>
     <Git onClick={gitHandler} className='ml-16 w-32 h-32'/>

   </div>
   </div>


   </section>
  );
}

export default Skills;
