import './App.css';

import Name from './components/Name';
import AboutMe from './components/AboutMe';
import PokemonApp from './components/PokemonApp';
import Qnn from './components/Qnn';
import Skills from './components/Skills';
import { useState, useEffect } from 'react';

function App() {

  const [offsetY, setOffsetY] =  useState(0); // used to store scrolling amount data

  const handleScroll = () => setOffsetY(window.pageYOffset)


  useEffect(() => {
     window.addEventListener('scroll', handleScroll);
     return () => window.removeEventListener('scroll', handleScroll); // not to overdo! we dont want it to listen 
     // while not scrolling at all
   }, [])

  console.log(offsetY)

  return (
    <>
    <Name/>
    <AboutMe
      offsetY = {setOffsetY}
    />

    <section>
      <div className='h-screen flex items-center justify-center'>
          <p className='w-5/6 text-center text-7xl '>
            “The only way to do a <span className='text-pink-600'>great work</span> is  to 
            <span className='text-pink-600'> love</span> what you do.”
          </p>
      </div>
    </section>
    <PokemonApp/>

    <section>
      <div className='h-screen flex items-center justify-center'>
          <p className='w-4/6 text-center text-7xl '>
            “People who are serious about <span className='text-purple-600'>software</span> should make their 
            <span className='text-purple-600'> own hardware</span>.”
          </p>
      </div>
    </section>
    <Qnn/>

    <Skills/>

    </>
  );
}

export default App;
